mapper.select:
SELECT  FROM link_url_manager WHERE isDelete = 1


 AND city_id = #{cityId,jdbcType=VARCHAR}


 AND link_name = #{linkName,jdbcType=VARCHAR}


 AND link_url = #{linkUrl,jdbcType=VARCHAR}


 AND contacts = #{contacts,jdbcType=VARCHAR}


 AND phone = #{phone,jdbcType=VARCHAR}


 AND isshow = #{isshow,jdbcType=INTEGER}


 AND orderby = #{orderby,jdbcType=INTEGER}


 AND remarks = #{remarks,jdbcType=LONGVARCHAR}


  AND source = #{source,jdbcType=INTEGER}


  AND source = #{cityName,jdbcType=VARCHAR}
