mapper.select:
SELECT   FROM  popular WHERE  1 = 1

  AND  id = #{id}


  AND cate_id = #{cateId}


  AND menu_id = #{menuId}


  AND name = #{name}


  AND source = #{source,jdbcType=INTEGER}


  AND url = #{url,jdbcType=VARCHAR}


  AND provinceId = #{provinceid,jdbcType=INTEGER}


  AND cityId = #{cityid,jdbcType=INTEGER}


  AND areaId = #{areaid,jdbcType=INTEGER}


  AND sheetId = #{sheetid,jdbcType=INTEGER}


  AND isDelete = #{isDelete}
.
